sys_script_include.script:
var TomTestDevOps = Class.create();
TomTestDevOps.prototype = {
	initialize: function() {
	},

	getVersion: function() {
		var sAppId = gs.getCurrentApplicationId();
		var sAppName = gs.getCurrentScopeName();
		var sVersion = 'N/A';
		var grApp = new GlideRecord('sys_app');
		if (grApp.get(sAppId)) {
			sVersion = grApp.getValue('version');
		} // if app
		return sAppName + ' ' + sVersion;
	}, // function getVersion

	type: 'TomTestDevOps'
};
